            171 => 
            array (
                'id' => 28173,
                'capitulo_id' => 1051,
                'pericopa_id' => 2275,
                'num_versiculo' => 8,
                'contenido' => 'Mas Dios demuestra su amor para con nosotros, en que, siendo aún pecadores, Cristo murió por nosotros.',
                'referencia' => 'Romanos 5:8',
                'imagen' => '',
