                'capitulo_id' => 1057,
                'pericopa_id' => 2286,
                'num_versiculo' => 9,
                'contenido' => 'Y David dice: Séales vuelta su mesa en trampa y en red, y en piedra de tropiezo, y en retribución para ellos.',
                'referencia' => 'Romanos 11:9',
                'imagen' => '',
                'pie_imagen' => '',
                'video' => '',
                'created_at' => '2024-03-12 15:25:33',
                'updated_at' => '2024-03-12 15:25:33',
