            360 => 
            array (
                'id' => 28362,
                'capitulo_id' => 1057,
                'pericopa_id' => 2288,
                'num_versiculo' => 35,
                'contenido' => '¿O quién le dio a él primero, para que le fuese recompensado?',
                'referencia' => 'Romanos 11:35',
                'imagen' => '',
                'pie_imagen' => '',
                'video' => '',
                'created_at' => '2024-03-12 15:25:33',
                'updated_at' => '2024-03-12 15:25:33',
