                'capitulo_id' => 1058,
                'pericopa_id' => 2289,
                'num_versiculo' => 7,
                'contenido' => 'o si de servicio, en servir; o el que enseña, en la enseñanza;',
                'referencia' => 'Romanos 12:7',
                'imagen' => '',
                'pie_imagen' => '',
                'video' => '',
                'created_at' => '2024-03-12 15:25:33',
                'updated_at' => '2024-03-12 15:25:33',
