            41 => 
            array (
                'id' => 28543,
                'capitulo_id' => 1065,
                'pericopa_id' => 2303,
                'num_versiculo' => 14,
                'contenido' => 'Si permanece la obra de alguno que sobreedificó, recibirá recompensa.',
                'referencia' => '1 Corintios 3:14',
                'imagen' => '',
                'pie_imagen' => '',
                'video' => '',
                'created_at' => '2024-03-12 15:25:33',
                'updated_at' => '2024-03-12 15:25:33',
