            42 => 
            array (
                'id' => 28544,
                'capitulo_id' => 1065,
                'pericopa_id' => 2303,
                'num_versiculo' => 15,
                'contenido' => 'Si la obra de alguno fuere quemada, sufrirá pérdida; pero él mismo será salvo, aunque así como por fuego.',
                'referencia' => '1 Corintios 3:15',
