            64 => 
            array (
                'id' => 28566,
                'capitulo_id' => 1066,
                'pericopa_id' => 2304,
                'num_versiculo' => 14,
                'contenido' => 'No escribo esto para avergonzaros, sino para amonestaros como a mis hijos amados.',
                'referencia' => '1 Corintios 4:14',
                'imagen' => '',
                'pie_imagen' => '',
                'video' => '',
                'created_at' => '2024-03-12 15:25:33',
                'updated_at' => '2024-03-12 15:25:33',
