            65 => 
            array (
                'id' => 28567,
                'capitulo_id' => 1066,
                'pericopa_id' => 2304,
                'num_versiculo' => 15,
                'contenido' => 'Porque aunque tengáis diez mil ayos en Cristo, no tendréis muchos padres, porque en Cristo Jesús yo os engendré por medio del evangelio.',
                'referencia' => '1 Corintios 4:15',
                'imagen' => '',
                'pie_imagen' => '',
                'video' => '',
                'created_at' => '2024-03-12 15:25:33',
                'updated_at' => '2024-03-12 15:25:33',
