            66 => 
            array (
                'id' => 28568,
                'capitulo_id' => 1066,
                'pericopa_id' => 2304,
                'num_versiculo' => 16,
                'contenido' => 'Por tanto, os ruego que me imitéis.',
                'referencia' => '1 Corintios 4:16',
                'imagen' => '',
                'pie_imagen' => '',
                'video' => '',
                'created_at' => '2024-03-12 15:25:33',
                'updated_at' => '2024-03-12 15:25:33',
            ),
            67 => 
            array (
                'id' => 28569,
                'capitulo_id' => 1066,
                'pericopa_id' => 2304,
                'num_versiculo' => 17,
                'contenido' => 'Por lo cual os he enviado a Timoteo, que es mi hijo amado y fiel en el Señor, el que os recordará mis caminos en Cristo, de la manera que enseño en todas partes y en todas las iglesias.',
                'referencia' => '1 Corintios 4:17',
                'imagen' => '',
                'pie_imagen' => '',
                'video' => '',
                'created_at' => '2024-03-12 15:25:33',
                'updated_at' => '2024-03-12 15:25:33',
            ),
            68 => 
            array (
                'id' => 28570,
                'capitulo_id' => 1066,
                'pericopa_id' => 2304,
                'num_versiculo' => 18,
                'contenido' => 'Mas algunos están envanecidos, como si yo nunca hubiese de ir a vosotros.',
                'referencia' => '1 Corintios 4:18',
