            427 => 
            array (
                'id' => 28929,
                'capitulo_id' => 1079,
                'pericopa_id' => 2326,
                'num_versiculo' => 10,
                'contenido' => 'el que nos libró y nos libra de tan gran muerte; y en quien esperamos que aún nos librará;',
                'referencia' => '2 Corintios 1:10',
                'imagen' => '',
                'pie_imagen' => '',
                'video' => '',
                'created_at' => '2024-03-12 15:25:33',
                'updated_at' => '2024-03-12 15:25:33',
            ),
            428 => 
            array (
                'id' => 28930,
                'capitulo_id' => 1079,
                'pericopa_id' => 2326,
                'num_versiculo' => 11,
                'contenido' => 'cooperando también vosotros con oración por nosotros, para que por muchas personas sean dadas gracias a favor nuestro por el don concedido a nosotros por medio de muchos.',
