            447 => 
            array (
                'id' => 28949,
                'capitulo_id' => 1080,
                'pericopa_id' => 2329,
                'num_versiculo' => 6,
                'contenido' => 'A tal persona le basta esta reprensión hecha por muchos;',
                'referencia' => '2 Corintios 2:6',
