            456 => 
            array (
                'id' => 28958,
                'capitulo_id' => 1080,
                'pericopa_id' => 2331,
                'num_versiculo' => 15,
                'contenido' => 'Porque para Dios somos olor grato de Cristo en los que se salvan, y en los que se pierden;',
                'referencia' => '2 Corintios 2:15',
