            457 => 
            array (
                'id' => 28959,
                'capitulo_id' => 1080,
                'pericopa_id' => 2331,
                'num_versiculo' => 16,
                'contenido' => 'a éstos ciertamente olor de muerte para muerte, y a aquéllos olor de vida para vida. Y para estas cosas, ¿quién es suficiente?',
                'referencia' => '2 Corintios 2:16',
                'imagen' => '',
                'pie_imagen' => '',
                'video' => '',
                'created_at' => '2024-03-12 15:25:33',
