            array (
                'id' => 28960,
                'capitulo_id' => 1080,
                'pericopa_id' => 2331,
                'num_versiculo' => 17,
                'contenido' => 'Porque no somos, como muchos, mercaderes que corrompen la palabra de Dios, sino que con sinceridad, como de parte de Dios, delante de Dios, hablamos en Cristo.',
                'referencia' => '2 Corintios 2:17',
