            459 => 
            array (
                'id' => 28961,
                'capitulo_id' => 1081,
                'pericopa_id' => 2332,
                'num_versiculo' => 1,
                'contenido' => '¿Comenzamos otra vez a recomendarnos a nosotros mismos? ¿O tenemos necesidad, como algunos, de cartas de recomendación para vosotros, o de recomendación de vosotros?',
                'referencia' => '2 Corintios 3:1',
                'imagen' => '',
                'pie_imagen' => '',
                'video' => '',
                'created_at' => '2024-03-12 15:25:33',
                'updated_at' => '2024-03-12 15:25:33',
            ),
