            array (
                'id' => 28962,
                'capitulo_id' => 1081,
                'pericopa_id' => 2332,
                'num_versiculo' => 2,
                'contenido' => 'Nuestra carta sois vosotros, escrita en nuestros corazones, conocida y leída por todos los hombres;',
                'referencia' => '2 Corintios 3:2',
                'imagen' => '',
                'pie_imagen' => '',
                'video' => '',
                'created_at' => '2024-03-12 15:25:33',
                'updated_at' => '2024-03-12 15:25:33',
