            463 => 
            array (
                'id' => 28965,
                'capitulo_id' => 1081,
                'pericopa_id' => 2332,
                'num_versiculo' => 5,
                'contenido' => 'No que seamos capaces por nosotros mismos para pensar algo de nosotros mismos, sino que nuestra capacidad es de Dios;',
                'referencia' => '2 Corintios 3:5',
                'imagen' => '',
                'pie_imagen' => '',
                'video' => '',
                'created_at' => '2024-03-12 15:25:33',
                'updated_at' => '2024-03-12 15:25:33',
            ),
            464 => 
            array (
                'id' => 28966,
                'capitulo_id' => 1081,
                'pericopa_id' => 2332,
                'num_versiculo' => 6,
                'contenido' => 'el que asimismo nos hizo siervos capaces de un nuevo convenio, no de la letra, sino del espíritu, porque la letra mata, pero el espíritu vivifica.',
                'referencia' => '2 Corintios 3:6',
