                'id' => 28972,
                'capitulo_id' => 1081,
                'pericopa_id' => 2332,
                'num_versiculo' => 12,
                'contenido' => 'Así que, teniendo tal esperanza, hablamos con mucha franqueza;',
                'referencia' => '2 Corintios 3:12',
                'imagen' => '',
                'pie_imagen' => '',
                'video' => '',
                'created_at' => '2024-03-12 15:25:33',
                'updated_at' => '2024-03-12 15:25:33',
