            27 => 
            array (
                'id' => 29029,
                'capitulo_id' => 1084,
                'pericopa_id' => 2337,
                'num_versiculo' => 11,
                'contenido' => 'Nuestra boca se ha abierto a vosotros, oh corintios; nuestro corazón se ha ensanchado.',
                'referencia' => '2 Corintios 6:11',
                'imagen' => '',
                'pie_imagen' => '',
