            28 => 
            array (
                'id' => 29030,
                'capitulo_id' => 1084,
                'pericopa_id' => 2337,
                'num_versiculo' => 12,
                'contenido' => 'No estáis limitados por nosotros, sino que estáis limitados por vuestro propio corazón.',
                'referencia' => '2 Corintios 6:12',
                'imagen' => '',
                'pie_imagen' => '',
                'video' => '',
                'created_at' => '2024-03-12 15:25:33',
