            31 => 
            array (
                'id' => 29033,
                'capitulo_id' => 1084,
                'pericopa_id' => 2338,
                'num_versiculo' => 15,
                'contenido' => '¿Y qué concordia tiene Cristo con Belial? ¿O qué parte tiene el creyente con el incrédulo?',
                'referencia' => '2 Corintios 6:15',
                'imagen' => '',
                'pie_imagen' => '',
                'video' => '',
                'created_at' => '2024-03-12 15:25:33',
                'updated_at' => '2024-03-12 15:25:33',
