            182 => 
            array (
                'id' => 29184,
                'capitulo_id' => 1092,
                'pericopa_id' => 2351,
                'num_versiculo' => 7,
                'contenido' => 'No es que haya otro, sino que hay algunos que os perturban, y quieren pervertir el evangelio de Cristo.',
