            186 => 
            array (
                'id' => 29188,
                'capitulo_id' => 1092,
                'pericopa_id' => 2352,
                'num_versiculo' => 11,
                'contenido' => 'Pero os hago saber, hermanos, que el evangelio que ha sido anunciado por mí no es según el hombre;',
                'referencia' => 'Gálatas 1:11',
                'imagen' => '',
                'pie_imagen' => '',
                'video' => '',
                'created_at' => '2024-03-12 15:25:33',
