                'id' => 29676,
                'capitulo_id' => 1111,
                'pericopa_id' => 2401,
                'num_versiculo' => 14,
                'contenido' => 'Os saluda Lucas, el médico amado, y Demas.',
                'referencia' => 'Colosenses 4:14',
                'imagen' => '',
                'pie_imagen' => '',
