            175 => 
            array (
                'id' => 29677,
                'capitulo_id' => 1111,
                'pericopa_id' => 2401,
                'num_versiculo' => 15,
                'contenido' => 'Saludad a los hermanos que están en Laodicea, y a Ninfas y a la iglesia que está en su casa.',
                'referencia' => 'Colosenses 4:15',
