            178 => 
            array (
                'id' => 29680,
                'capitulo_id' => 1111,
                'pericopa_id' => 2401,
                'num_versiculo' => 18,
                'contenido' => 'Esta salutación es de mi propia mano, de Pablo. Acordaos de mis prisiones. La gracia sea con vosotros. Amén.',
                'referencia' => 'Colosenses 4:18',
                'imagen' => '',
