            array (
                'id' => 30234,
                'capitulo_id' => 1142,
                'pericopa_id' => 2464,
                'num_versiculo' => 5,
                'contenido' => 'y sobre ella los querubines de gloria que cubrían el propiciatorio; de estas cosas no se puede ahora hablar en detalle.',
