            235 => 
            array (
                'id' => 30236,
                'capitulo_id' => 1142,
                'pericopa_id' => 2464,
                'num_versiculo' => 7,
                'contenido' => 'pero en la segunda parte, sólo el sumo sacerdote entraba una vez al año, no sin sangre, la cual ofrecía por sí mismo y por los pecados del pueblo cometidos en la ignorancia,',
                'referencia' => 'Hebreos 9:7',
                'imagen' => '',
                'pie_imagen' => '',
                'video' => '',
                'created_at' => '2024-03-12 15:25:34',
                'updated_at' => '2024-03-12 15:25:34',
