            236 => 
            array (
                'id' => 30237,
                'capitulo_id' => 1142,
                'pericopa_id' => 2464,
                'num_versiculo' => 8,
                'contenido' => 'dando el Espíritu Santo a entender con esto que aún no se había manifestado el camino al Lugar Santísimo, entre tanto que la primera parte del tabernáculo estuviese en pie.',
                'referencia' => 'Hebreos 9:8',
