            238 => 
            array (
                'id' => 30239,
                'capitulo_id' => 1142,
                'pericopa_id' => 2464,
                'num_versiculo' => 10,
                'contenido' => 'consistiendo sólo en comidas y en bebidas, y en diversos lavamientos y ordenanzas para el cuerpo, impuestas hasta el tiempo de la renovación.',
                'referencia' => 'Hebreos 9:10',
                'imagen' => '',
                'pie_imagen' => '',
                'video' => '',
                'created_at' => '2024-03-12 15:25:35',
                'updated_at' => '2024-03-12 15:25:35',
