            array (
                'id' => 30342,
                'capitulo_id' => 1145,
                'pericopa_id' => 2468,
                'num_versiculo' => 6,
                'contenido' => 'Porque el Señor al que ama, disciplina, y azota a todo el que recibe como hijo.',
