                'pericopa_id' => 2537,
                'num_versiculo' => 12,
                'contenido' => 'Y me volví para ver la voz que hablaba conmigo; y al volverme, vi siete candeleros de oro;',
                'referencia' => 'Apocalipsis 1:12',
                'imagen' => '',
                'pie_imagen' => '',
