            array (
                'id' => 31093,
                'capitulo_id' => 1183,
                'pericopa_id' => 2562,
                'num_versiculo' => 14,
                'contenido' => 'porque son espíritus de demonios, que hacen señales, y van a los reyes de la tierra y de todo el mundo, para congregarlos para la batalla de aquel gran día del Dios Todopoderoso.',
