            100 => 
            array (
                'id' => 31101,
                'capitulo_id' => 1184,
                'pericopa_id' => 2563,
                'num_versiculo' => 1,
                'contenido' => 'Y vino uno de los siete ángeles que tenían las siete copas, y habló conmigo, diciendo: Ven acá, y te mostraré la condenación de la gran ramera, la cual está sentada sobre muchas aguas,',
                'referencia' => 'Apocalipsis 17:1',
                'imagen' => '',
                'pie_imagen' => '',
                'video' => '',
                'created_at' => '2024-03-12 15:25:35',
                'updated_at' => '2024-03-12 15:25:35',
            ),
            101 => 
            array (
                'id' => 31102,
                'capitulo_id' => 1184,
                'pericopa_id' => 2563,
                'num_versiculo' => 2,
                'contenido' => 'con la que han fornicado los reyes de la tierra, y los que moran en la tierra se han embriagado con el vino de su fornicación.',
                'referencia' => 'Apocalipsis 17:2',
                'imagen' => '',
                'pie_imagen' => '',
                'video' => '',
                'created_at' => '2024-03-12 15:25:35',
                'updated_at' => '2024-03-12 15:25:35',
