            103 => 
            array (
                'id' => 31104,
                'capitulo_id' => 1184,
                'pericopa_id' => 2563,
                'num_versiculo' => 4,
                'contenido' => 'Y la mujer estaba vestida de púrpura y de escarlata, y adornada de oro, y de piedras preciosas y de perlas, y tenía en la mano un cáliz de oro lleno de abominaciones y de la inmundicia de su fornicación;',
                'referencia' => 'Apocalipsis 17:4',
                'imagen' => '',
                'pie_imagen' => '',
                'video' => '',
                'created_at' => '2024-03-12 15:25:35',
                'updated_at' => '2024-03-12 15:25:35',
            ),
            104 => 
            array (
                'id' => 31105,
                'capitulo_id' => 1184,
                'pericopa_id' => 2563,
                'num_versiculo' => 5,
                'contenido' => 'y en su frente había un nombre escrito: MISTERIO, BABILONIA LA GRANDE, LA MADRE DE LAS RAMERAS Y DE LAS ABOMINACIONES DE LA TIERRA.',
                'referencia' => 'Apocalipsis 17:5',
