            array (
                'id' => 31191,
                'capitulo_id' => 1188,
                'pericopa_id' => 2571,
                'num_versiculo' => 13,
                'contenido' => 'Al oriente tres puertas; al norte tres puertas; al sur tres puertas; al poniente tres puertas.',
