            194 => 
            array (
                'id' => 31195,
                'capitulo_id' => 1188,
                'pericopa_id' => 2571,
                'num_versiculo' => 17,
                'contenido' => 'Y midió su muro: ciento cuarenta y cuatro codos, según medida de hombre, la cual era la del ángel.',
                'referencia' => 'Apocalipsis 21:17',
