            197 => 
            array (
                'id' => 31198,
                'capitulo_id' => 1188,
                'pericopa_id' => 2571,
                'num_versiculo' => 20,
                'contenido' => 'el quinto de ónice, el sexto de cornalina, el séptimo de crisólito, el octavo de berilo, el noveno de topacio, el décimo de crisoprasa, el undécimo de jacinto y el duodécimo de amatista.',
                'referencia' => 'Apocalipsis 21:20',
                'imagen' => '',
                'pie_imagen' => '',
                'video' => '',
                'created_at' => '2024-03-12 15:25:36',
                'updated_at' => '2024-03-12 15:25:36',
            ),
            198 => 
            array (
                'id' => 31199,
                'capitulo_id' => 1188,
                'pericopa_id' => 2571,
                'num_versiculo' => 21,
                'contenido' => 'Y las doce puertas eran doce perlas; cada una de las puertas era una perla. Y la calle de la ciudad era de oro puro, como vidrio transparente.',
                'referencia' => 'Apocalipsis 21:21',
                'imagen' => '',
