            478 => 
            array (
                'id' => 31979,
                'capitulo_id' => 1216,
                'pericopa_id' => 3369,
                'num_versiculo' => 13,
                'contenido' => 'Y aconteció que comenzamos a prosperar en extremo, y a multiplicarnos en el país.',
                'referencia' => '2 Nefi 5:13',
                'imagen' => '',
