            367 => 
            array (
                'id' => 32868,
                'capitulo_id' => 1253,
                'pericopa_id' => 2593,
                'num_versiculo' => 15,
                'contenido' => 'Y entrego estas planchas en manos de mi hijo Omni, para que se lleven según los mandamientos de mis padres.',
                'referencia' => 'Jarom 1:15',
                'imagen' => '',
                'pie_imagen' => '',
