            51 => 
            array (
                'id' => 34052,
                'capitulo_id' => 1295,
                'pericopa_id' => 4906,
                'num_versiculo' => 38,
                'contenido' => 'Luego Zeezrom de nuevo le dijo: ¿Es el Hijo de Dios el mismo Padre Eterno?',
                'referencia' => 'Alma 11:38',
                'imagen' => 'imagenes/El Cristo resucitado.jpg',
                'pie_imagen' => 'El Cristo resucitado, por Wilson J. Omg',
                'video' => '',
                'created_at' => '2024-03-12 15:25:39',
                'updated_at' => '2024-03-12 15:25:39',
