            14 => 
            array (
                'id' => 36515,
                'capitulo_id' => 1375,
                'pericopa_id' => 3993,
                'num_versiculo' => 45,
                'contenido' => 'para que seáis hijos de vuestro Padre que está en los cielos; pues él hace salir su sol sobre los malos y sobre los buenos.',
                'referencia' => '3 Nefi 12:45',
