            ),
            16 => 
            array (
                'id' => 36517,
                'capitulo_id' => 1375,
                'pericopa_id' => 4841,
                'num_versiculo' => 47,
                'contenido' => 'Las cosas antiguas han pasado, y todas las cosas se han vuelto nuevas.',
                'referencia' => '3 Nefi 12:47',
