            17 => 
            array (
                'id' => 36518,
                'capitulo_id' => 1375,
                'pericopa_id' => 4840,
                'num_versiculo' => 48,
                'contenido' => 'Por tanto, quisiera que fueseis perfectos así como yo, o como vuestro Padre que está en los cielos es perfecto.',
                'referencia' => '3 Nefi 12:48',
                'imagen' => '',
                'pie_imagen' => '',
                'video' => '',
                'created_at' => '2024-03-12 15:25:42',
