                'id' => 36528,
                'capitulo_id' => 1376,
                'pericopa_id' => 4842,
                'num_versiculo' => 10,
                'contenido' => 'Sea hecha tu voluntad en la tierra así como en el cielo.',
                'referencia' => '3 Nefi 13:10',
                'imagen' => '',
                'pie_imagen' => '',
                'video' => '',
                'created_at' => '2024-03-12 15:25:42',
                'updated_at' => '2024-03-12 15:25:42',
