                'capitulo_id' => 1376,
                'pericopa_id' => 4842,
                'num_versiculo' => 13,
                'contenido' => 'Porque tuyo es el reino, y el poder, y la gloria, para siempre. Amén.',
                'referencia' => '3 Nefi 13:13',
                'imagen' => '',
                'pie_imagen' => '',
                'video' => '',
                'created_at' => '2024-03-12 15:25:42',
                'updated_at' => '2024-03-12 15:25:42',
