            38 => 
            array (
                'id' => 36539,
                'capitulo_id' => 1376,
                'pericopa_id' => 4844,
                'num_versiculo' => 21,
                'contenido' => 'Porque donde esté vuestro tesoro, allí estará también vuestro corazón.',
                'referencia' => '3 Nefi 13:21',
                'imagen' => '',
                'pie_imagen' => '',
                'video' => '',
                'created_at' => '2024-03-12 15:25:42',
                'updated_at' => '2024-03-12 15:25:42',
