            225 => 
            array (
                'id' => 36726,
                'capitulo_id' => 1383,
                'pericopa_id' => 4882,
                'num_versiculo' => 3,
                'contenido' => 'Y sucedió que partió pan de nuevo y lo bendijo, y dio de comer a los discípulos.',
                'referencia' => '3 Nefi 20:3',
                'imagen' => '',
                'pie_imagen' => '',
                'video' => '',
                'created_at' => '2024-03-12 15:25:42',
                'updated_at' => '2024-03-12 15:25:42',
