            array (
                'id' => 36727,
                'capitulo_id' => 1383,
                'pericopa_id' => 4882,
                'num_versiculo' => 4,
                'contenido' => 'Y cuando hubieron comido, les mandó que partieran pan, y dieran a la multitud;',
                'referencia' => '3 Nefi 20:4',
