            array (
                'id' => 36728,
                'capitulo_id' => 1383,
                'pericopa_id' => 4882,
                'num_versiculo' => 5,
                'contenido' => 'y cuando hubieron dado a la multitud, les dio también vino para que bebiesen, y les mandó que dieran a la multitud.',
                'referencia' => '3 Nefi 20:5',
